<?php

namespace Gubsocosta\Orm\Entity;

/**
 * Class Student
 * 
 * @package Gubsocosta\Orm\Entity
 * @Entity
 */
class Student
{
    /**
     * @Id
     * @GeneratedValue
     * @Column (type="integer")
     */
    private $id;

    /**
     * @Column (type="string")
     */
    private $name;

    public function getId(): int
    {
        return $this->id;
    }

    public function getName(): string
    {
        return $this->name;
    }
    
    public function setName(string $name): self
    {
        $this->name = $name;
        
        return $this;
    }
}
